import React, { Component } from 'react';
import FullScreen from 'react-request-fullscreen';

const HomeCard = ({children}) => {
	return <div className="card" >{children}</div>
}

class Home extends Component {
	constructor() {
		super()
		this.state = {
			isFullScreen: false
		}
	}

	toggleFullScreen = isFullScreen => {
		this.setState({ isFullScreen });
	}

	buildBoard() {
		return <FullScreen ref={ref => this.fullScreenRef = ref} onFullScreenChange={this.toggleFullScreen}>
		    <div className="game-board card" onClick={() => this.fullScreenRef.fullScreen()}>
		    </div>
		</FullScreen>
	}

	render() {
		let board = this.buildBoard();
	    return (<div className="Home">
	    	{board}
	    </div>);
	}
}

export default Home;
