import React, { Component } from 'react';

const Card = (props) => {
	return <a
		target="_blank"
		href={props.url}
		style={{left: props.positions.left, top: props.positions.top}}
		onMouseDown={() => props.move(props.card)}
		onMouseUp={() => props.place(props.card)}
		className={"display-card " + props.class}></a>
}

class Projects extends Component {
	constructor() {
		super()
		this.state = {
			cards: [
				{top: 100, left: 300},
				{top: 200, left: 100},
				{top: 600, left: 100}
			],
			urls: [
				'https://www.toolboxproject.io',
				'https://amaclean2.github.io',
				'https://devfestsf.com'
			]
		}
	}

	moveCards = (e, card) => {
		let positions = this.state.positions;
		console.log(e.target.style.left, e.clientX, e.target.style.top, e.clientY);
	}

	placeCards = (e, card) => {
		let positions = this.state.positions;
		positions[card].left = e.clientX;
		positions[card].top = e.clientY;

		this.setState({ positions });
	}

	render() {
	    return (<div className="Projects">
	    	<Card
	    		positions={this.state.cards[0]}
	    		card={0}
	    		move={this.moveCards}
	    		place={this.placeCards}
	    		url={this.state.urls[0]}
	    		class={'toolbox'} />
	    	<Card
	    		positions={this.state.cards[1]}
	    		card={1}
	    		move={this.moveCards}
	    		place={this.placeCards}
	    		url={this.state.urls[1]}
	    		class={'lessonbox'} />
	    	<Card
	    		positions={this.state.cards[2]}
	    		card={2}
	    		move={this.moveCards}
	    		place={this.placeCards}
	    		url={this.state.urls[2]}
	    		class={'devfest'} />
	    </div>);
	}
}

export default Projects;
