import React, { Component } from 'react';

class Game extends Component {
	constructor() {
		super()
		this.state = {
			boardSize: {
				width: 100,
				height: 100
			},
			boardPosition: {
				x: 0,
				y: 0
			}
		}
		this.boardParams = React.createRef();
	}

	moveBoard = e => {
		let boardPosition = this.state.boardPosition,
			screen = this.boardParams.current,
			screenX = screen.getBoundingClientRect().x,
			screenY = screen.getBoundingClientRect().y;


		if (e.clientX !== 0 || e.clientY !== 0) {
			boardPosition.x = e.clientX - screenX;
			boardPosition.y = e.clientY - screenY;
		}

		this.setState({ boardPosition });
	}

	drawBoard() {
		return <div
			className="board"
			draggable={true}
			onDrag={this.moveBoard}
			style={{
				width: this.state.boardSize.width,
				height: this.state.boardSize.height,
				top: this.state.boardPosition.y,
				left: this.state.boardPosition.x
			}}>

		</div>
	}

	render() {
		let board = this.drawBoard();
	    return (<div className="Game" ref={this.boardParams} >
	    	{board}
	    </div>);
	}
}

export default Game;
