import React, { Component } from 'react';
import FullScreen from 'react-request-fullscreen';
import { MdFullscreen } from 'react-icons/md';

const HomeCard = ({children}) => {
	return <div className="card" >{children}</div>
}

class Home extends Component {
	constructor() {
		super()
		this.state = {
			isFullScreen: false
		}
	}

	toggleFullScreen = isFullScreen => {
		this.setState({ isFullScreen });
	}

	buildBoard() {
		return <FullScreen ref={ref => this.elFullScreenRef = ref} onFullScreenChange={this.toggleFullScreen}>
		    <div
		    	className="game-board card"
		    	ref={ref => this.elRef = ref}>
		    	<button
		    		onClick={() => this.elFullScreenRef.fullScreen(this.elRef)}>
		    			{this.state.isFullScreen ? <MdFullscreen /> : 'Enter Full Screen'}
		    	</button>
		    </div>
		</FullScreen>
	}

	render() {
		let board = this.buildBoard();
	    return (<div className="Home">
	    	{board}
	    </div>);
	}
}

export default Home;
