	}

	drawBoard() {
		return <div
			className="board"
			onDrag={this.moveBoard}
			style={{
				width: this.state.boardSize.width,
				height: this.state.boardSize.height,
				top: this.state.boardPosition.x,
				left: this.state.boardPosition.y
			}}>

		</div>
	}

	render() {
		let board = this.drawBoard();
	    return (<div className="Game">
	    	{board}
	    </div>);
	}
}

export default Game;
